import { StatusBar } from 'expo-status-bar';
import React, {useState} from 'react';
import firestore, { QuerySnapshot, getFirestore, collection } from 'firebase/firestore';
import { doc, onSnapshot } from "firebase/firestore";
import { FlatList, StyleSheet, View } from 'react-native';
import { initializeApp } from 'firebase/app';
import { ScrollView, Text } from 'react-native';
import { Appbar, TextInput, Button } from 'react-native-paper';
import database from 'firebase/firestore'
import Todo from './Todo';

const firebaseConfig = {
  apiKey: "AIzaSyCbfPuPqy7pa8nhhhhtLt7pCQx3VGOc5qg",
  authDomain: "ktgk-dc13b.firebaseapp.com",
  projectId: "ktgk-dc13b",
  storageBucket: "ktgk-dc13b.appspot.com",
  messagingSenderId: "782280285325",
  appId: "1:782280285325:web:c108a2a8c2512ee63996c4",
  measurementId: "G-2D7YJZWFBG"
};

initializeApp(firebaseConfig);

function App(){
  const db = getFirestore()
  const ref = collection(db,'todos')
  const [todo,setTodo] = React.useState('')
  const [loading, setLoading] = React.useState(true)
  const [todos, setTodos] = React.useState([])
  async function AddToDO (){
    await ref.add({
      title:todo,
      complete:false
    })
    setTodo('');
  }

  React.useEffect(() =>{
    const unscribe = onSnapshot(ref, (querySnapshot) => {
      const list = [];
      QuerySnapshot.forEach(doc =>{
        const {title, complete} = doc.data();
        list.push({
          id: doc.id,
          title,
          complete,
        })
      })
    })
    setTodos[List]
    if (loading){
      setLoading(false)
    }
  })
  if (loading){
    return null
  }
  return (
    <View style={{flex:1}}>
        <Appbar>
          <Appbar.Content title ={'TODO LIST'}></Appbar.Content>
        </Appbar>
        <FlatList style={{flex:1}} data={todos} keyExtractor={(item)=> item.id} renderItem={({item}) => <Todo{...item}></Todo>}></FlatList>
        <TextInput label={'New todo'} value={todo} onChangeText={(text)=>{setTodo(text)}}></TextInput>
        <Button onPress={AddToDO}>Add TODO</Button>
    </View>
  )
}

export default App
